    ResultSet rs;
    String bustype,dest,frm,too,d8,name,travels,seat,tckt, fare;
    public DisplayTicket() {
        initComponents();
        try{
        Class.forName("com.mysql.jdbc.Driver");
        Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/busmanagementsystem","root","");
        PreparedStatement ps=con.prepareStatement("select *from ticket where username=(select userid from session) and tcktid=?; ");
        PreparedStatement ps1=con.prepareStatement("select count(*) from ticket where username=(select userid from session) ;");
        PreparedStatement ps3=con.prepareStatement("select dept,bustype from bus where fare=(select selected from session)");
        rs=ps1.executeQuery();
        if(rs.next())
        {
            tckt="T"+Integer.toString(rs.getInt("count(*)")+1);
        }
        ps1=con.prepareStatement("update ticket set tcktid=? where tcktid='na';");
        ps1.setString(1, tckt);
        ps1.execute();
        ps.setString(1,tckt);
        rs=ps3.executeQuery();
        if(rs.next())
        {
            bustype=rs.getString("bustype");
            dest=rs.getString("dept");
        }
        
        rs=ps.executeQuery();
        if(rs.next())
        {
            frm=rs.getString("frm");
            too=rs.getString("too");
            name=rs.getString("name");
            travels=rs.getString("travels");
            seat=rs.getString("seat");
            fare=Integer.toString(rs.getInt("fare"));
            d8=rs.getString("d8");
        }
        
        }
        catch (ClassNotFoundException | SQLException ex) {
            Logger.getLogger(WelcomePage.class.getName()).log(Level.SEVERE, null, ex);
        }
        u1.setText(frm+" to "+too);
        u2.setText(d8);
        u3.setText(travels);
        u4.setText(name);
        u5.setText(tckt);
        u6.setText(seat);
        u7.setText(bustype);
        u8.setText(dest);
        u10.setText(fare);
      
        }
    private void makePanelImage(Component panel)
    {
        Dimension size = panel.getSize();
        BufferedImage image = new BufferedImage(
                    size.width, size.height 
                              , BufferedImage.TYPE_INT_RGB);
        Graphics2D g2 = image.createGraphics();
        panel.paint(g2);
        try
        {
            ImageIO.write(image, "png", new File("F:\\TicketGenerator\\Ticket"+tckt+".png"));
            System.out.println("Panel saved as Image.");
        }
        catch(Exception e)
        {
            e.printStackTrace();
        }
    }
  

